mapper.select:
 	SELECT COUNT(1) FROM white_user
	
		
	 		
			AND user_id = #{text}
			
			
			AND user_name = #{text}

		SELECT 
			USER_SEQ AS no,
			user_id AS userId,
			user_name AS userName
 		FROM white_user
 		
	 		
		 		
				AND user_id = #{text}
				
				
				AND user_name = #{text}
				
			
		
 		Limit ${pagePre}, ${pageSize}

SELECT LM.LOWER_SEQ AS lowerSeq, LM.UPPER_SEQ AS upperSeq, LM.LOWER_NM AS lowerNm, LM.LOWER_URL AS lowerUrl, LM.AUTH_SEQ AS authSeq, LM.SHOW_YN AS showYn, LM.LOWER_ORDER AS lowerOrder FROM LOWER_MENU AS LM
WHERE LM.UPPER_SEQ = #{upperSeq}
ORDER BY LM.LOWER_ORDER

mapper.delete:
DELETE FROM POS_AUTH
WHERE USER_SEQ = #{userNo}
AND AUTH_SEQ IN (${remove})

mapper.update:
UPDATE UPPER_MENU SET UPPER_NM = CASE
WHEN UPPER_SEQ = #{item.upperSeq} THEN #{item.upperNm}
END, UPPER_URL = CASE
WHEN UPPER_SEQ = #{item.upperSeq} THEN #{item.upperUrl}
END, AUTH_SEQ = CASE
WHEN UPPER_SEQ = #{item.upperSeq} THEN #{item.authSeq}
END, SHOW_YN = CASE
WHEN UPPER_SEQ = #{item.upperSeq} THEN #{item.showYn}
END, UPPER_ORDER = CASE
WHEN UPPER_SEQ = #{item.upperSeq} THEN #{item.upperOrder}
END WHERE UPPER_SEQ IN
#{item.upperSeq}

mapper.select:
SELECT COUNT(1) FROM LOWER_MENU
WHERE UPPER_SEQ IN

	#{item.upperSeq}

mapper.delete:
DELETE FROM UPPER_MENU
WHERE UPPER_SEQ IN 

	#{item.upperSeq}

mapper.update:
UPDATE LOWER_MENU SET UPPER_SEQ = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.upperSeq}
END, LOWER_NM = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.lowerNm}
END, LOWER_URL = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.lowerUrl}
END, AUTH_SEQ = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.authSeq}
END, SHOW_YN = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.showYn}
END, LOWER_ORDER = CASE
WHEN LOWER_SEQ = #{item.lowerSeq} THEN #{item.lowerOrder}
END WHERE LOWER_SEQ IN
#{item.lowerSeq}

mapper.delete:
DELETE FROM LOWER_MENU
WHERE LOWER_SEQ IN 

	#{item.lowerSeq}

mapper.update:
UPDATE AUTH SET AUTH_NM = CASE
WHEN AUTH_SEQ = #{item.authSeq} THEN #{item.authNm}
END, AUTH_CMT = CASE
WHEN AUTH_SEQ = #{item.authSeq} THEN #{item.authCmt}
END, AUTH_ORDER = CASE
WHEN AUTH_SEQ = #{item.authSeq} THEN #{item.authOrder}
END WHERE AUTH_SEQ IN
#{item.authSeq}

mapper.select:
SELECT
(
	SELECT COUNT(1) FROM POS_AUTH
	WHERE AUTH_SEQ IN
	
		#{item.authSeq}
			
)+
(
	SELECT COUNT(1) FROM nav_menu
	WHERE AUTH_SEQ IN
	
		#{item.authSeq}
			
)+
(
	SELECT COUNT(1) FROM side_menu
	WHERE AUTH_SEQ IN
	
		#{item.authSeq}
			
) AS count

