mapper.select:
SELECT COUNT(1) FROM white_user
WHERE 1=1

AND user_id = #{srhId}


AND user_name = #{srhNm}

		SELECT 
			user_seq AS userSeq,
			user_id AS userId,
			user_name AS userName
 		FROM white_user
 		WHERE 1=1
 		
		AND user_id = #{srhId}
		
		
		AND user_name = #{srhNm}
		
 		Limit ${pagePre}, ${pageCnt}

mapper.update:
UPDATE nav_menu SET nav_nm = CASE
WHEN nav_seq = #{item.navSeq} THEN #{item.navNm}
END, nav_url = CASE
WHEN nav_seq = #{item.navSeq} THEN #{item.navUrl}
END, auth_nm_seq = CASE
WHEN nav_seq = #{item.navSeq} THEN #{item.authNmSeq}
END, nav_order = CASE
WHEN nav_seq = #{item.navSeq} THEN #{item.navOrder}
END WHERE nav_seq IN
#{item.navSeq}

mapper.delete:
DELETE FROM authority
WHERE auth_seq IN 

	#{item.authSeq}

DELETE FROM nav_menu
WHERE nav_seq IN 

	#{item.navSeq}
