mapper.select:
SELECT

	(
	  SELECT 
	    SUM( 
	      CASE
	      WHEN bank_seq = #{item.bankSeq} THEN money
	      WHEN move_seq = #{item.bankSeq} THEN ABS(money)
	      END
	    )
	  FROM money_record_re
	  WHERE user_seq = #{item.userSeq} 
	  AND record_date  <   #{item.startDate}
	)AS ${item.bankName}

SELECT COUNT(1) FROM money_record_re
WHERE pur_seq IN

	#{item.purSeq}

SELECT COUNT(1) FROM money_record_re
WHERE pur_dtl_seq IN

	#{item.purDtlSeq}

SELECT COUNT(1) FROM money_record_re
WHERE bank_seq IN

	#{item.bankSeq}

mapper.update:
UPDATE money_record_re SET record_date = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.recordDate}
END, content = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.content}
END, position = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.position}
END, pur_seq = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.purSeq}
END, pur_dtl_seq = CASE
WHEN record_seq = #{item.recordSeq} THEN null
WHEN record_seq = #{item.recordSeq} THEN #{item.purDtlSeq}
END, bank_seq = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.bankSeq}
END, move_seq = CASE
WHEN record_seq = #{item.recordSeq} THEN NULL
WHEN record_seq = #{item.recordSeq} THEN #{item.moveSeq}
END, money = CASE
WHEN record_seq = #{item.recordSeq} THEN #{item.money}
END WHERE record_seq IN
#{item.recordSeq}

UPDATE purpose SET pur_order = CASE
WHEN pur_seq = #{item.purSeq} THEN #{item.purOrder}
END, purpose = CASE
WHEN pur_seq = #{item.purSeq} THEN #{item.purpose}
END WHERE pur_seq IN
#{item.purSeq}

UPDATE purpose_detail SET pur_seq = CASE
WHEN pur_dtl_seq = #{item.purDtlSeq} THEN #{item.purSeq}
END, pur_dtl_order = CASE
WHEN pur_dtl_seq = #{item.purDtlSeq} THEN #{item.purDtlOrder}
END, pur_detail = CASE
WHEN pur_dtl_seq = #{item.purDtlSeq} THEN #{item.purDetail}
END WHERE pur_dtl_seq IN
#{item.purDtlSeq}

UPDATE user_bank SET bank_name = CASE
WHEN bank_seq = #{item.bankSeq} THEN #{item.bankName}
END, bank_account = CASE
WHEN bank_seq = #{item.bankSeq} THEN #{item.bankAccount}
END, bank_now_use_yn = CASE
WHEN bank_seq = #{item.bankSeq} THEN #{item.bankNowUseYn}
END WHERE bank_seq IN
#{item.bankSeq}

mapper.delete:
DELETE FROM money_record_re
WHERE record_seq IN 

	#{item.recordSeq}

DELETE FROM purpose
WHERE pur_seq IN 

	#{item.purSeq}

DELETE FROM purpose_detail
WHERE pur_dtl_seq IN 

	#{item.purDtlSeq}

DELETE FROM user_bank
WHERE bank_seq IN 

	#{item.bankSeq}
