mapper.select:
/* mainMapper|selectUserPasswd */
SELECT					
	USER_PASS userPass
FROM WHITE_USER		
WHERE USER_ID = #{userId}

	/* mainMapper|getUserSeq */
	SELECT 
  		USER_SEQ AS userSeq
	FROM WHITE_USER
	WHERE USER_ID = #{userId}

/* mainMapper|userIdCheck */
SELECT COUNT(1)
FROM white_user
WHERE USER_ID = #{userId}

/* mainMapper|selectUserAuthority */
SELECT 
  A.AUTH_NM AS authNm,
  IF(
		(
			SELECT PA.POS_AUTH_SEQ FROM POS_AUTH PA
			WHERE PA.AUTH_SEQ = A.AUTH_SEQ
			AND USER_SEQ = #{userSeq}
			LIMIT 1
		) IS NULL, 0, 1
  ) AS auth
FROM AUTH A
