mapper.select:
SELECT sb.source_seq AS sourceSeq, c.code_nm AS codeNm, sb.user_seq AS userSeq, sb.title, DATE_FORMAT(sb.reg_date,'%Y-%m-%d %H:%i:%s') AS regDate FROM source_board sb LEFT OUTER JOIN code c ON sb.code_seq = c.code_seq
WHERE c.code_key = #{codeKey}
ORDER BY sb.source_seq DESC Limit ${pagePre}, ${pageCnt}
