mapper.select:
		SELECT COUNT(1) FROM source_board sb
		LEFT OUTER JOIN code c
	    	ON sb.code_seq = c.code_seq
	    LEFT OUTER JOIN white_user wu
  			ON wu.user_seq = sb.user_seq
		
  			
		  	AND c.code_key = #{codeKey}
		  	
		  	
		  	
			AND wu.user_id = #{text}
		  	
		  	
			AND sb.title LIKE #{text}
		  	
		  	
		  	AND sb.content LIKE #{text}

SELECT sb.source_seq AS sourceSeq, c.code_nm AS codeNm, sb.user_seq AS userSeq, wu.user_id AS userId, sb.title, DATE_FORMAT(sb.reg_date,'%Y-%m-%d %H:%i:%s') AS regDate FROM source_board sb LEFT OUTER JOIN code c ON sb.code_seq = c.code_seq LEFT OUTER JOIN white_user wu ON wu.user_seq = sb.user_seq
AND c.code_key = #{codeKey}
AND wu.user_id = #{text}
AND sb.title LIKE #{text}
AND sb.content LIKE #{text}
ORDER BY sb.source_seq DESC Limit ${pagePre}, ${pageSize}
