mapper.select:
		SELECT COUNT(1) FROM source_board sb
		LEFT OUTER JOIN cmm_code cc
	    	ON sb.lang_cd = cc.code
	    LEFT OUTER JOIN white_user wu
  			ON wu.user_seq = sb.user_seq
		
  			
		  	AND sb.lang_cd = #{langCd}
		  	
		  	
		  	
			AND wu.user_id = #{text}
		  	
		  	
			AND sb.title LIKE #{text}
		  	
		  	
		  	AND sb.content LIKE #{text}

SELECT sb.source_seq AS sourceSeq, cc.code_nm AS langNm, sb.user_seq AS userSeq, wu.user_id AS userId, sb.title, DATE_FORMAT(sb.reg_date,'%Y-%m-%d %H:%i:%s') AS regDate FROM source_board sb LEFT OUTER JOIN cmm_code cc ON sb.lang_cd = cc.code LEFT OUTER JOIN white_user wu ON wu.user_seq = sb.user_seq
AND sb.lang_cd = #{langCd}
AND wu.user_id = #{text}
AND sb.title LIKE #{text}
AND sb.content LIKE #{text}
ORDER BY sb.source_seq DESC Limit ${pagePre}, ${pageSize}

mapper.update:
UPDATE source_board
SET	title = #{title},
	lang_cd = #{langCd},
	content = #{content}
WHERE user_seq = #{userSeq}
AND source_seq = #{sourceSeq}

mapper.delete:
DELETE FROM source_board
WHERE user_seq = #{userSeq}
AND source_seq = #{sourceSeq}
