mapper.select:
/*whiteMapper|selectSortTable*/
SELECT
	${firstSeqNm} AS firstSeq			
	,${columnNm} AS col
FROM ${tableNm}

	
	AND ${secondSeqNm} = #{secondSeq}
	
	
	AND USER_SEQ = #{userSeq}
	


ORDER BY ${columnNm} ASC

mapper.update:
/*whiteMapper|updateSortTable*/
UPDATE ${tableNm}
SET ${columnNm} =
CASE

	WHEN ${firstSeqNm} = #{item.firstSeq} THEN #{item.col}

END
WHERE ${firstSeqNm} IN 

	#{item.firstSeq}


AND USER_SEQ = #{userSeq}
